import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PartsPage() {
  const [parts, setParts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchParts = async () => {
      try {
        const response = await axios.get('/api/vendor/parts');
        setParts(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch parts');
        setLoading(false);
      }
    };

    fetchParts();
  }, []);

  if (loading) return <div className="p-4">Loading...</div>;
  if (error) return <div className="p-4 text-red-500">{error}</div>;

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">My Parts Listings</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {parts.map((part) => (
          <div key={part.id} className="border rounded-lg p-4 shadow-sm">
            {part.imageUrl && (
              <img 
                src={part.imageUrl} 
                alt={part.name}
                className="w-full h-48 object-cover rounded-md mb-2" 
              />
            )}
            <h3 className="text-lg font-semibold">{part.name}</h3>
            <p className="text-gray-600">Part #: {part.partNumber}</p>
            <p className="text-gray-600">Stock: {part.stockCount}</p>
            <p className="text-lg font-bold mt-2">â‚µ{part.price}</p>
            <div className="mt-2 space-x-2">
              <button className="bg-blue-500 text-white px-3 py-1 rounded">
                Edit
              </button>
              <button className="bg-red-500 text-white px-3 py-1 rounded">
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PartsPage;
